import React, { useState } from 'react';
import './JsonComparison.css';

function JsonComparison() {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);
  const [comparisonResult, setComparisonResult] = useState(null);
  const [error, setError] = useState(null);
  const [expandedPages, setExpandedPages] = useState({});

  const handleFileChange = (event, setFile) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const json = JSON.parse(e.target.result);
        setFile(json);
        setError(null);
      } catch (error) {
        console.error('Error parsing JSON:', error);
        setError('Error parsing JSON file. Please ensure it\'s a valid JSON.');
      }
    };
    reader.readAsText(file);
  };

  const handleCompare = async () => {
    if (!file1 || !file2) {
      setError('Please select both JSON files');
      return;
    }

    try {
      const response = await fetch('http://localhost:3001/compare', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ file1, file2 }),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const result = await response.json();
      console.log('Comparison result:', result);
      setComparisonResult(result);
      setError(null);
    } catch (error) {
      console.error('Error comparing files:', error);
      setError(`An error occurred while comparing the files: ${error.message}`);
    }
  };

  const togglePage = (page) => {
    setExpandedPages(prev => ({ ...prev, [page]: !prev[page] }));
  };

  const renderJsonWithHighlights = (json, diffs, isOldSite) => {
    const sortedEntries = Object.entries(json).sort(([a], [b]) => normalizeUrl(a).localeCompare(normalizeUrl(b)));

    return sortedEntries.map(([page, links], pageIndex) => {
      const normalizedPage = normalizeUrl(page);
      const sortedLinks = links.map(normalizeUrl).sort();
      const pageDiffs = diffs[normalizedPage] || {};

      return (
        <div key={`${isOldSite ? 'old' : 'new'}-${pageIndex}-${normalizedPage}`} className="page-section">
          <h4 className="page-url" onClick={() => togglePage(page)}>
            {expandedPages[page] ? '▼' : '▶'} Page: {page}
          </h4>
          {expandedPages[page] && (
            <div className="page-content">
              <h5>Links on this page:</h5>
              <ul>
                {sortedLinks.map((url, index) => {
                  let className = '';
                  if (isOldSite && pageDiffs.missing && pageDiffs.missing.includes(url)) {
                    className = 'missing';
                  } else if (!isOldSite && pageDiffs.extra && pageDiffs.extra.includes(url)) {
                    className = 'added';
                  }

                  return (
                    <li key={`${isOldSite ? 'old' : 'new'}-${pageIndex}-${index}-${url}`} className={className}>
                      {url}
                    </li>
                  );
                })}
              </ul>
            </div>
          )}
        </div>
      );
    });
  };

  const normalizeUrl = (url) => {
    try {
      const parsedUrl = new URL(url);
      let path = parsedUrl.pathname;
      // Remove the hash and everything after it
      path = path.split('#')[0];
      path = removeTrailingSlash(path);
      return path.startsWith('/') ? path.slice(1) : path;
    } catch (error) {
      console.error('Error normalizing URL:', url, error);
      return url;
    }
  };

  const removeTrailingSlash = (path) => {
    return path.endsWith('/') ? path.slice(0, -1) : path;
  };

  return (
    <div className="json-comparison">
      <div className="file-inputs">
        <div>
          <label htmlFor="file1">Old Website JSON:</label>
          <input
            id="file1"
            type="file"
            onChange={(e) => handleFileChange(e, setFile1)}
            accept=".json"
          />
        </div>
        <div>
          <label htmlFor="file2">New Website JSON:</label>
          <input
            id="file2"
            type="file"
            onChange={(e) => handleFileChange(e, setFile2)}
            accept=".json"
          />
        </div>
      </div>
      <button onClick={handleCompare}>Compare</button>
      {error && <div className="error">{error}</div>}
      {comparisonResult && (
        <div className="results">
          <h2>Comparison Results:</h2>
          <div className="json-display">
            <div className="old-site">
              <h3>Old Website Content:</h3>
              {renderJsonWithHighlights(comparisonResult.oldSite, comparisonResult.differences, true)}
            </div>
            <div className="new-site">
              <h3>New Website Content:</h3>
              {renderJsonWithHighlights(comparisonResult.newSite, comparisonResult.differences, false)}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default JsonComparison;
