import React, { useState, useEffect } from 'react';
import { 
  Button, 
  Container, 
  Grid, 
  Paper, 
  Typography, 
  Box,
  TextField,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Switch,
  FormControlLabel,
  CircularProgress,
  LinearProgress
} from '@mui/material';
import CompareArrowsIcon from '@mui/icons-material/CompareArrows';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import DownloadIcon from '@mui/icons-material/Download';
import './JsonComparison.css';

function JsonComparison() {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);
  const [comparisonResult, setComparisonResult] = useState(null);
  const [error, setError] = useState(null);
  const [expandedPages, setExpandedPages] = useState({});
  const [showOnlyDifferences, setShowOnlyDifferences] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [serverPort, setServerPort] = useState(3001);

  useEffect(() => {
    findServerPort();
  }, []);

  const findServerPort = async () => {
    for (let port = 3001; port < 3010; port++) {
      try {
        const response = await fetch(`http://localhost:${port}/health`, { method: 'GET' });
        if (response.ok) {
          setServerPort(port);
          console.log(`Server found on port ${port}`);
          return;
        }
      } catch (error) {
        // Port is not available, continue to the next one
      }
    }
    console.error('Could not find an available server port');
  };

  const handleFileChange = (event, setFile) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const json = JSON.parse(e.target.result);
        setFile(json);
        setError(null);
      } catch (error) {
        console.error('Error parsing JSON:', error);
        setError('Error parsing JSON file. Please ensure it\'s a valid JSON.');
      }
    };
    reader.readAsText(file);
  };

  const handleCompare = async () => {
    if (!file1 || !file2) {
      setError('Please select both JSON files');
      return;
    }

    setIsLoading(true);
    setError(null);
    setProgress(0);

    try {
      console.log('Starting comparison...');
      const response = await fetch(`http://localhost:${serverPort}/compare`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ file1, file2 }),
      });

      const reader = response.body.getReader();
      const decoder = new TextDecoder();

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        const messages = chunk.split('\n').filter(Boolean);

        for (const message of messages) {
          try {
            const data = JSON.parse(message);
            if (data.progress) {
              setProgress(data.progress);
            } else if (data.result) {
              console.log('Comparison result:', data.result);
              setComparisonResult(data.result);
              setIsLoading(false);
              return;
            }
          } catch (error) {
            console.error('Error parsing chunk:', error);
          }
        }
      }
    } catch (error) {
      console.error('Error comparing files:', error);
      setError(`An error occurred while comparing the files: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const togglePage = (page) => {
    setExpandedPages(prev => {
      const newState = { ...prev, [page]: !prev[page] };
      // Find the corresponding page in the other column
      const otherPage = Object.keys(comparisonResult.differences).find(key => 
        normalizeUrl(key) === normalizeUrl(page)
      );
      if (otherPage) {
        newState[otherPage] = newState[page];
      }
      return newState;
    });
  };

  const renderJsonWithHighlights = (json, diffs, isOldSite) => {
    const domain = isOldSite ? getDomain(Object.keys(json)[0]) : getDomain(Object.keys(comparisonResult.newSite)[0]);
    const uniquePages = new Map();

    Object.entries(json).forEach(([page, links]) => {
      const normalizedPage = normalizeUrl(page, domain);
      if (!normalizedPage.includes('#')) {
        if (!uniquePages.has(normalizedPage)) {
          uniquePages.set(normalizedPage, links);
        } else {
          uniquePages.get(normalizedPage).push(...links);
        }
      }
    });

    const sortedEntries = Array.from(uniquePages.entries()).sort(([a], [b]) => a.localeCompare(b));

    return sortedEntries.map(([page, links], pageIndex) => {
      const sortedLinks = [...new Set(links.map(link => normalizeUrl(link, domain)))]
        .filter(link => link.trim() !== '')
        .sort();
      const pageDiffs = diffs[page] || {};

      if (sortedLinks.length === 0) {
        return null;
      }

      const filteredLinks = showOnlyDifferences
        ? sortedLinks.filter(url => 
            (isOldSite && pageDiffs.missing && pageDiffs.missing.includes(url)) ||
            (!isOldSite && pageDiffs.extra && pageDiffs.extra.includes(url))
          )
        : sortedLinks;

      if (filteredLinks.length === 0) {
        return null;
      }

      return (
        <Accordion 
          key={`${isOldSite ? 'old' : 'new'}-${pageIndex}-${page}`} 
          className="page-section"
          expanded={expandedPages[page] || false}
          onChange={() => togglePage(page)}
        >
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-controls={`panel${pageIndex}-content`}
            id={`panel${pageIndex}-header`}
          >
            <Typography className="page-url">
              {`${domain}/${page}`}
            </Typography>
          </AccordionSummary>
          <AccordionDetails>
            <ul>
              {filteredLinks.map((url, index) => {
                let className = '';
                if (isOldSite && pageDiffs.missing && pageDiffs.missing.includes(url)) {
                  className = 'missing';
                } else if (!isOldSite && pageDiffs.extra && pageDiffs.extra.includes(url)) {
                  className = 'added';
                } else if (isOldSite && pageDiffs.redirected && pageDiffs.redirected.some(r => r.from === url)) {
                  className = 'redirected';
                }

                return (
                  <li key={`${isOldSite ? 'old' : 'new'}-${pageIndex}-${index}-${url}`} className={className}>
                    {url}
                    {isOldSite && pageDiffs.redirected && pageDiffs.redirected.some(r => r.from === url) && (
                      <span className="redirect-arrow"> → {pageDiffs.redirected.find(r => r.from === url).to}</span>
                    )}
                  </li>
                );
              })}
            </ul>
          </AccordionDetails>
        </Accordion>
      );
    }).filter(Boolean);
  };

  const normalizeUrl = (url, domain) => {
    try {
      // If the url is just a path (slug), prepend it with the domain
      if (!url.startsWith('http://') && !url.startsWith('https://')) {
        url = `https://${domain}/${url}`;
      }
      const parsedUrl = new URL(url);
      let path = parsedUrl.pathname;
      path = removeTrailingSlash(path);
      return path.startsWith('/') ? path.slice(1) : path;
    } catch (error) {
      console.error('Error normalizing URL:', url, error);
      return url;
    }
  };

  const removeTrailingSlash = (path) => {
    return path.endsWith('/') ? path.slice(0, -1) : path;
  };

  const getDomain = (url) => {
    try {
      return new URL(url).hostname;
    } catch (error) {
      console.error('Error parsing URL:', url, error);
      return null;
    }
  };

  const generateDifferencesJson = () => {
    if (!comparisonResult) return null;

    const differences = {};
    Object.entries(comparisonResult.differences).forEach(([page, diffs]) => {
      if (diffs.missing && diffs.missing.length > 0) {
        differences[page] = diffs.missing;
      }
    });

    return JSON.stringify(differences, null, 2);
  };

  const handleDownload = () => {
    const differencesJson = generateDifferencesJson();
    if (!differencesJson) return;

    const blob = new Blob([differencesJson], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'missing_links.json';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const renderDifferences = (differences) => {
    return Object.entries(differences).map(([page, diff]) => (
      <div key={page}>
        <h3>{page}</h3>
        {diff.missing.length > 0 && (
          <div>
            <h4>Missing:</h4>
            <ul>
              {diff.missing.map((link, index) => (
                <li key={index} style={{ color: 'red' }}>{link}</li>
              ))}
            </ul>
          </div>
        )}
        {diff.redirected.length > 0 && (
          <div>
            <h4>Redirected:</h4>
            <ul>
              {diff.redirected.map((redirect, index) => (
                <li key={index} style={{ color: 'orange' }}>
                  {redirect.from} → {redirect.to}
                </li>
              ))}
            </ul>
          </div>
        )}
        {diff.extra.length > 0 && (
          <div>
            <h4>Extra:</h4>
            <ul>
              {diff.extra.map((link, index) => (
                <li key={index} style={{ color: 'green' }}>{link}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    ));
  };

  return (
    <Container maxWidth="xl" className="json-comparison">
      <Paper elevation={3} className="input-section">
        <Typography variant="h4" gutterBottom>
          JSON Comparison Tool
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} md={5}>
            <TextField
              fullWidth
              type="file"
              onChange={(e) => handleFileChange(e, setFile1)}
              accept=".json"
              label="Old Website JSON"
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
          <Grid item xs={12} md={2} className="compare-icon">
            <CompareArrowsIcon fontSize="large" />
          </Grid>
          <Grid item xs={12} md={5}>
            <TextField
              fullWidth
              type="file"
              onChange={(e) => handleFileChange(e, setFile2)}
              accept=".json"
              label="New Website JSON"
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
        </Grid>
        <Box mt={2} display="flex" flexDirection="column" alignItems="center" gap={2}>
          <Box display="flex" justifyContent="center" gap={2} alignItems="center">
            <Button 
              variant="contained" 
              color="primary" 
              onClick={handleCompare}
              startIcon={<CompareArrowsIcon />}
              disabled={isLoading}
            >
              Compare
            </Button>
            {isLoading && <CircularProgress size={24} />}
            {comparisonResult && (
              <Button 
                variant="contained" 
                color="secondary" 
                onClick={handleDownload}
                startIcon={<DownloadIcon />}
              >
                Download Differences
              </Button>
            )}
          </Box>
          {isLoading && (
            <Box width="100%" maxWidth={400}>
              <LinearProgress variant="determinate" value={progress} />
              <Typography variant="body2" color="textSecondary" align="center">
                {`${Math.round(progress)}%`}
              </Typography>
            </Box>
          )}
        </Box>
      </Paper>

      {error && (
        <Paper elevation={3} className="error-section">
          <Typography color="error">{error}</Typography>
        </Paper>
      )}

      {comparisonResult && (
        <Paper elevation={3} className="results-section">
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
            <FormControlLabel
              control={
                <Switch
                  checked={showOnlyDifferences}
                  onChange={(e) => setShowOnlyDifferences(e.target.checked)}
                  color="primary"
                />
              }
              label="Show only differences"
            />
          </Box>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <Typography variant="h6">Old Website Content:</Typography>
              {renderJsonWithHighlights(comparisonResult.oldSite, comparisonResult.differences, true)}
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="h6">New Website Content:</Typography>
              {renderJsonWithHighlights(comparisonResult.newSite, comparisonResult.differences, false)}
            </Grid>
          </Grid>
        </Paper>
      )}
    </Container>
  );
}

export default JsonComparison;
