import React, { useState } from 'react';
import './JsonComparison.css';

function JsonComparison() {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);
  const [differences, setDifferences] = useState(null);
  const [oldSiteContent, setOldSiteContent] = useState(null);
  const [newSiteContent, setNewSiteContent] = useState(null);

  const handleFileChange = (event, setFile, setSiteContent) => {
    const file = event.target.files[0];
    setFile(file);
    const reader = new FileReader();
    reader.onload = (e) => setSiteContent(JSON.parse(e.target.result));
    reader.readAsText(file);
  };

  const handleCompare = async () => {
    if (!file1 || !file2) {
      alert('Please select both JSON files');
      return;
    }

    const formData = new FormData();
    formData.append('file1', file1);
    formData.append('file2', file2);

    try {
      const response = await fetch('http://localhost:3001/compare', {
        method: 'POST',
        body: formData,
      });
      const result = await response.json();
      setDifferences(result);
    } catch (error) {
      console.error('Error comparing files:', error);
      alert('An error occurred while comparing the files');
    }
  };

  const renderJsonWithHighlights = (json, diffs) => {
    return Object.entries(json).map(([key, value]) => {
      let className = '';
      if (diffs && diffs[key]) {
        className = diffs[key].type === 'missing' ? 'missing' : 'added';
      }
      return (
        <div key={key} className={className}>
          <strong>{key}:</strong> {JSON.stringify(value)}
        </div>
      );
    });
  };

  return (
    <div className="json-comparison">
      <div className="file-inputs">
        <div>
          <label htmlFor="file1">Old Website JSON:</label>
          <input
            id="file1"
            type="file"
            onChange={(e) => handleFileChange(e, setFile1, setOldSiteContent)}
            accept=".json"
          />
        </div>
        <div>
          <label htmlFor="file2">New Website JSON:</label>
          <input
            id="file2"
            type="file"
            onChange={(e) => handleFileChange(e, setFile2, setNewSiteContent)}
            accept=".json"
          />
        </div>
      </div>
      <button onClick={handleCompare}>Compare</button>
      {differences && (
        <div className="results">
          <h2>Comparison Results:</h2>
          <div className="json-display">
            <div className="old-site">
              <h3>Old Website Content:</h3>
              {renderJsonWithHighlights(oldSiteContent, differences)}
            </div>
            <div className="new-site">
              <h3>New Website Content:</h3>
              {renderJsonWithHighlights(newSiteContent, differences)}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default JsonComparison;
