import React, { useState } from 'react';
import './JsonComparison.css';

function JsonComparison() {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);
  const [comparisonResult, setComparisonResult] = useState(null);

  const handleFileChange = (event, setFile) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const json = JSON.parse(e.target.result);
        setFile(json);
      } catch (error) {
        console.error('Error parsing JSON:', error);
        alert('Error parsing JSON file. Please ensure it\'s a valid JSON.');
      }
    };
    reader.readAsText(file);
  };

  const handleCompare = async () => {
    if (!file1 || !file2) {
      alert('Please select both JSON files');
      return;
    }

    try {
      const response = await fetch('http://localhost:3001/compare', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ file1, file2 }),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const result = await response.json();
      console.log('Comparison result:', result);
      setComparisonResult(result);
    } catch (error) {
      console.error('Error comparing files:', error);
      alert(`An error occurred while comparing the files: ${error.message}`);
    }
  };

  const renderJsonWithHighlights = (json, diffs, isOldSite) => {
    return Object.entries(json).map(([page, links]) => (
      <div key={page}>
        <h4>{page}</h4>
        <ul>
          {links.map((slug, index) => {
            let className = '';
            let displaySlug = slug;
            if (diffs && diffs[page] && diffs[page][index]) {
              const diff = diffs[page][index];
              if (isOldSite) {
                if (diff.type === 'missing' || diff.type === 'different') {
                  className = 'missing';
                  displaySlug = diff.type === 'different' ? diff.oldValue : diff.value;
                }
              } else {
                if (diff.type === 'extra' || diff.type === 'different') {
                  className = 'added';
                  displaySlug = diff.type === 'different' ? diff.newValue : diff.value;
                }
              }
            }
            console.log(`Rendering ${page} - ${index}: ${displaySlug} with class ${className}`);
            return (
              <li key={index} className={className}>
                {displaySlug}
              </li>
            );
          })}
        </ul>
      </div>
    ));
  };

  return (
    <div className="json-comparison">
      <div className="file-inputs">
        <div>
          <label htmlFor="file1">Old Website JSON:</label>
          <input
            id="file1"
            type="file"
            onChange={(e) => handleFileChange(e, setFile1)}
            accept=".json"
          />
        </div>
        <div>
          <label htmlFor="file2">New Website JSON:</label>
          <input
            id="file2"
            type="file"
            onChange={(e) => handleFileChange(e, setFile2)}
            accept=".json"
          />
        </div>
      </div>
      <button onClick={handleCompare}>Compare</button>
      {comparisonResult && (
        <div className="results">
          <h2>Comparison Results:</h2>
          <div className="json-display">
            <div className="old-site">
              <h3>Old Website Content:</h3>
              {renderJsonWithHighlights(comparisonResult.oldSite, comparisonResult.differences, true)}
            </div>
            <div className="new-site">
              <h3>New Website Content:</h3>
              {renderJsonWithHighlights(comparisonResult.newSite, comparisonResult.differences, false)}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default JsonComparison;
